resources.string:
Drop Table If Exists [client_info];
CREATE TABLE 'client_info' (
'client_id' VARCHAR(255) PRIMARY KEY NOT NULL,
'last_sync_success' VARCHAR(100) default NULL);
Drop Table If Exists [object_values];
CREATE TABLE 'object_values' (
 'id' INTEGER PRIMARY KEY NOT NULL,
 'token' varchar(30) default NULL,
 'source_id' int(11) default NULL,
 'attrib' varchar(255) default NULL,
 'object' varchar(255) default NULL,
 'value' text default NULL,
 'update_type' varchar(255) default NULL,
 'attrib_type' varchar(255) default NULL);
Drop Table If Exists [sources];
CREATE TABLE 'sources' (
'id' INTEGER PRIMARY KEY,
'token' varchar(30) default NULL,
'source_id' int(11) default -1,
'source_url' VARCHAR(255) default NULL,
'session' VARCHAR(255) default NULL,
'last_updated' int(11) default 0,
'last_inserted_size' int(11) default 0,
'last_deleted_size' int(11) default 0,
'last_sync_duration' int(11) default 0,
'last_sync_success' int(11) default 0);
Drop Trigger If Exists [rhodeleteTrigger];
Drop Index  If Exists [by_src_type];
CREATE INDEX "by_src_type" ON "object_values" ("source_id", "attrib_type", "object");
Drop Index  If Exists [by_type];
CREATE INDEX "by_type" ON "object_values" ("attrib_type");

CREATE TRIGGER rhodeleteTrigger BEFORE DELETE ON object_values
FOR EACH ROW
BEGIN
SELECT rhoOnDeleteObjectRecord(OLD.value,OLD.attrib_type,OLD.update_type);
END;
